<script>
  const container = document.getElementById("mainContainer");
  const navbar = document.getElementById("navbar");
  const playbar = document.getElementById("playbar");

  const fullscreenBtns = document.querySelectorAll(".fullscreenBtn");
  const fullscreenImgs = document.querySelectorAll(".fullscreenimg");

  let expanded = false;

  function updateAllIcons() {
    fullscreenImgs.forEach((img) => {
      img.src = expanded
        ? "/public/icons/normal-screen.png"
        : "/public/icons/full-screen.png";
    });
  }

  fullscreenBtns.forEach((btn) => {
    btn.addEventListener("click", () => {
      expanded = !expanded;

      // üîÅ update ALL icons
      updateAllIcons();

      if (expanded) {
        // Expand
        container.style.height = "87vh";
        container.style.marginTop = "0";
        navbar.style.transform = "translateY(-110%)";
        // playbar.style.transform = "translateY(110%)";
      } else {
        // Restore
        container.style.height = "76vh";
        container.style.marginTop = "11vh";
        navbar.style.transform = "translateY(0)";
        // playbar.style.transform = "translateY(0)";
      }
    });
  });
</script>





